using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using UniquePlanners.Core.Entities;

namespace UniquePlanners.Infrastructure.Configuration
{
    public class PlannerCoversConfiguration : IEntityTypeConfiguration<PlannerCovers>
    {
        public void Configure(EntityTypeBuilder<PlannerCovers> builder)
        {
            builder.ToTable(nameof(PlannerCovers));

            builder.HasKey(x => x.Id);

            builder.Property(pc => pc.Cover).IsRequired();
            builder.Property(pc => pc.PlannerId).IsRequired();
            builder.Property(pc => pc.DateCreated).IsRequired();
            builder.Property(pc => pc.DateModified).IsRequired();
            builder.Property(pc => pc.IsDeleted).IsRequired().HasDefaultValue(false);

            builder.HasOne(pc => pc.Planner).WithMany(p => p.PlannerCovers).HasForeignKey(pc => pc.PlannerId).IsRequired();

            seedData(builder);
        }

        private void seedData(EntityTypeBuilder<PlannerCovers> builder)
        {
            List<PlannerCovers> plannerCovers = new List<PlannerCovers>
            {
                new PlannerCovers { Id = 1, PlannerId = 1, DateCreated = DateTime.Now, IsDeleted = false, DateModified = DateTime.Now, Cover = Convert.FromBase64String("") },
                new PlannerCovers { Id = 2, PlannerId = 2, DateCreated = DateTime.Now, IsDeleted = false, DateModified = DateTime.Now, Cover = Convert.FromBase64String("") },

            };

            builder.HasData(plannerCovers);
        }
    }
}
